let enter=document.querySelector(".user-block");let popup=document.querySelector(".modal_content");let close=popup.querySelector(".modal_content_close");let mapEnter=document.querySelector(".modal_map");let mapPopup=document.querySelector(".modal_content_map");let mapClose=mapPopup.querySelector(".modal_content_close");let form=popup.querySelector("form");let login=popup.querySelector("[name=login]");let password=popup.querySelector("[name=password]");let storage=localStorage.getItem("login");enter.addEventListener("click",function(event){event.preventDefault();popup.classList.add("display_block");login.focus();if(storage){login.value=storage;password.focus()}else{login.focus()}});close.addEventListener("click",function(event){event.preventDefault();popup.classList.remove("display_block");popup.classList.remove("modal-error")});form.addEventListener("submit",function(event){if(!login.value||!password.value){event.preventDefault();popup.classList.add("modal-error")}else{localStorage.setItem("login",login.value)}});window.addEventListener("keydown",function(event){if(event.keyCode===27){if(popup.classList.contains("modal_content")){popup.classList.remove("display_block");popup.classList.remove("modal-error")}}});mapEnter.addEventListener("click",function(event){event.preventDefault();mapPopup.classList.add("display_block")});mapClose.addEventListener("click",function(event){event.preventDefault();mapPopup.classList.remove("display_block")});window.addEventListener("keydown",function(event){if(event.keyCode===27){if(mapPopup.classList.contains("modal_content_map")){mapPopup.classList.remove("display_block")}}})